import React, { useState } from 'react';
import '../App.css';
import { useLocation } from 'react-router-dom';
import { axios } from 'axios';








export default function ProductForm() {

    const location = useLocation();
    console.log(location);
    const defaultImageSrc = location.state.imageSrc;

    const productAPI = (url = 'http://localhost:5000/api/ProductModels') => {
        return {
            fetchAll: () => axios.get(url),
            fetchById: id => axios.get(url + id),
            create: newRecord => axios.post(url, newRecord),
            update: (id, updatedRecord) => axios.put(url + id, updatedRecord),
            delete: id => axios.delete(url + id)
        }
    }
    const addOrEdit = (formData) => {
        productAPI().create(formData)
            .then(res => {
                
            })
            .catch(err => console.log(err))
    }

    const initalFieldValues = {
        ProductId: location.state.productId,
        Name: location.state.name,
        Artist: location.state.artist,
        ReleaseYear: location.state.releaseYear,
        AddDate: location.state.addDate,
        Country: location.state.country,
        Format: location.state.format,
        Genre: location.state.genre,
        UnitsInStock: location.state.unitsInStock,
        UnitsSold: location.state.unitsSold,
        imageName: location.state.imageName,
        imageSrc: location.state.imageSrc,
        imageFile: null,
        imageAlt: location.state.imageAlt
    }

    const [values, setValues] = useState(initalFieldValues);

    const handleInputChange = e => {
        const { name, value } = e.target;
        setValues({
            ...values,
            [name]: value
        })
    }

    const showPreview = e => {
        if (e.target.files && e.target.files[0]) {
            let imageFile = e.target.files[0];

            const reader = new FileReader();
            reader.onload = x => {
                setValues({
                    ...values,
                    imageFile,
                    imageSrc: x.target.result
                })
            }
            reader.readAsDataURL(imageFile)
        }
        else {

            setValues({
                ...values,
                Name: null,
                Artist: null,
                ReleaseYear: null,
                Country: null,
                Format: null,
                Genre: null,
                UnitsInStock: null,
                UnitsSold: "",
                imageName: null,
                imageFile: null,
                imageSrc: defaultImageSrc,
                imageAlt: null
            })
        }
    }

    const handleFormSubmit = e => {
        e.preventDefault()
        
            const formData = new FormData();
            formData.append('ProductId', values.ProductId)
            formData.append('Name', values.Name)
            formData.append('Artist', values.Artist)
            formData.append('ReleaseYear', values.ReleaseYear)
            formData.append('AddDate', values.AddDate)
            formData.append('Country', values.Country)
            formData.append('Format', values.Format)
            formData.append('Genre', values.Genre)
            formData.append('UnitsInStock', values.UnitsInStock)
            formData.append('UnitsSold', values.UnitsSold)
            formData.append('imageName', values.imageName)
            formData.append('imageFile', values.imageFile)
            formData.append('imageAlt', values.imageAlt)

            addOrEdit(formData)
        
    }









    return (
        <>
            <div className='container-fluid text-center'>
                <p className='lead'>A Product</p>
            </div>
            <form autoComplete='off' noValidate onSubmit={handleFormSubmit}>
                <div className='card'>
                    <img src={values.imageSrc} className="card-img-top img-fluid profilepiclarge mx-auto" alt='Product shown' style={{width: "250px"}} />
                    <div className='card-body'>
                        <div className='form-group'>
                            <input type="file" accept='image/*' className={'form-control-file'}
                                onChange={showPreview} id="image-uploader" />
                        </div>
                        <div className='form-group'>
                            <input className={'form-control margin-top-10'} placeholder='imageAlt' name="imageAlt" value={values.imageAlt} values={values.imageAlt} onChange={handleInputChange} id="imageAlt"></input>
                        </div>
                        <div className='form-group'>
                            <input className={'form-control margin-top-10'} placeholder='Name' name="Name" value={values.Name} values={values.Name} onChange={handleInputChange} id="Name"></input>
                        </div>
                        <div className='form-group'>
                            <input className={'form-control margin-top-10'} placeholder='Artist' name="Artist" value={values.Artist} values={values.Artist} onChange={handleInputChange} id="Artist"></input>
                        </div>
                        <div className='form-group'>
                            <input type="text" className={"form-control margin-top-10"} placeholder='YYYY-MM-DD' name="ReleaseYear" values={values.ReleaseYear} onChange={handleInputChange} id="ReleaseYear"></input>
                        </div>
                        <div className='form-group'>
                            <input className="form-control margin-top-10" placeholder='Country' name="Country" values={values.Country} onChange={handleInputChange} id="Country"></input>
                        </div>
                        <div className='form-group'>
                            <input className={"form-control margin-top-10"} placeholder='Format (CD, LP or Cassette)' name="Format" values={values.Format} onChange={handleInputChange} id="Format"></input>
                        </div>
                        <div className='form-group'>
                            <input className="form-control margin-top-10" placeholder='Genre' name="Genre" values={values.Genre} onChange={handleInputChange} id="Genre"></input>
                        </div>
                        <div className='form-group'>
                            <input className={"form-control margin-top-10"} placeholder='UnitsInStock' name="UnitsInStock" values={values.UnitsInStock} onChange={handleInputChange} id="UnitsInStock"></input>
                        </div>
                        <div className='form-group text-center margin-top-10'>
                            <button type='submit' className='btn btn-primary'>Submit</button>
                        </div>
                    </div>
                </div>
            </form>
        </>
    );
}
