import React from 'react';
import { Link } from 'react-router-dom';
import './NavMenu.css';
import { useAuth0 } from '@auth0/auth0-react';
import { Button, Offcanvas, Form, NavDropdown, Nav, Navbar, Stack, Image, Container } from 'react-bootstrap';

const NavMenuNew = () => {
    const { loginWithRedirect, logout } = useAuth0();
    const Authenticated = useAuth0().isAuthenticated;
    const Loading = useAuth0().isLoading;

    const loginButton = () => {
        if (Loading === true && Authenticated === false) {
            return (
                <Nav.Link className="text-dark" onClick={() => loginWithRedirect()}>Login</Nav.Link>
            )
        }
        if (Authenticated === true && Loading === false) {
            return (
                null
            )
        }
        if (Authenticated === false && Loading === false) {
            return (
                <Nav.Link className="text-dark" onClick={() => loginWithRedirect()}>Login</Nav.Link>
            )
        }
    }
    const logoutButton = () => {
        if (Loading === true && Authenticated === false) {
            return (
                null
            )
        }
        if (Authenticated === true && Loading === false) {
            return (
                <Nav.Link className="text-dark" onClick={() => logout()}>Logout</Nav.Link>
            )
        }
        if (Authenticated === false && Loading === false) {
            return (
                null
            )
        }
    }
    const profileButton = () => {
        if (Loading === true && Authenticated === false) {
            return (
                null
            )
        }
        if (Authenticated === true && Loading === false) {
            return (
                <Nav.Link as={Link} className="text-dark" to="/Profile">Profile</Nav.Link>
            )
        }
        if (Authenticated === false && Loading === false) {
            return (
                null
            )
        }
    }




    return (
        <header>
            <Navbar collapseOnSelect expand="lg" bg="dark" variant="dark">
                <Container>
                    <Navbar.Brand href="#home">React-Bootstrap</Navbar.Brand>
                    <Navbar.Toggle aria-controls="responsive-navbar-nav" />
                    <Navbar.Collapse id="responsive-navbar-nav">
                    <Stack gap={12} className='col-md-5 mx-auto'>
                    <Nav className="justify-content-center flex-grow-1 pe-3 nav1">
                        <Image />
                        <Stack direction='horizontal' gap={3}>
                            <Form.Control type="input" placeholder="Search" />
                            <Button variant="search" type="submit">
                                Search
                            </Button>
                        </Stack>
                    </Nav>
                    <Nav className="justify-content-center flex-grow-1 pe-3 nav2">
                        <Nav.Item>
                            <Nav.Link as={Link} className="text-dark" to="/" >Home</Nav.Link>
                        </Nav.Item>
                        <Nav.Item>
                            <Nav.Link as={Link} className="text-dark" to="/ProductList">ProductList</Nav.Link>
                        </Nav.Item>
                        <Nav.Item>
                            <Nav.Link as={Link} className="text-dark" to="/Details">Details</Nav.Link>
                        </Nav.Item>
                        <Nav.Item>
                            {profileButton()}
                        </Nav.Item>
                        <Nav.Item>
                            {loginButton()}
                        </Nav.Item>
                        <Nav.Item>
                            {logoutButton()}
                        </Nav.Item>
                    </Nav>
                </Stack>
                    </Navbar.Collapse>
                </Container>
            </Navbar>
            
        </header>
    )
}

export default NavMenuNew
