import Container from 'react-bootstrap/Container';
import './Details.css';
import { Image, Col, Row } from 'react-bootstrap'


export default function Details({ productId }) {

    const productAPI = (url = 'http://localhost:5000/api/ProductModels') => {
        return {
            fetchAll: () => axios.get(url),
            fetchById: id => axios.get(url + id),
            create: newRecord => axios.post(url, newRecord),
            update: (id, updatedRecord) => axios.put(url + id, updatedRecord),
            delete: id => axios.delete(url + id)
        }
    }



    return (
        <>
            <div>Details</div>
            <Container fluid className='container ms-auto me-auto'>
                <Row>
                    <Col md={6} className=' justify-content-center flex'>
                        <Image></Image>
                        1 of 2

                    </Col>
                    <Col md={6} className=' justify-content-center flex'>2 of 2</Col>
                </Row>
            </Container>
        </>
    )
}
